import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { RouterModule } from '@angular/router';
import { LoanService } from '../../services/loan.service';

@Component({
  selector: 'app-loan-simulator',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, RouterModule],
  templateUrl: './loan-simulator.component.html',
  styleUrls: ['./loan-simulator.component.css']
})
export class LoanSimulatorComponent {
  simulatorForm: FormGroup;
  simulationResult: any = null;

  constructor(
    private fb: FormBuilder,
    private loanService: LoanService
  ) {
    this.simulatorForm = this.fb.group({
      loanAmount: ['', [Validators.required, Validators.min(0)]],
      monthlyIncome: ['', [Validators.required, Validators.min(0)]],
      repaymentRatio: [0.3, [Validators.required, Validators.min(0), Validators.max(1)]]
    });
  }

  onSubmit() {
    if (this.simulatorForm.valid) {
      const { loanAmount, monthlyIncome, repaymentRatio } = this.simulatorForm.value;
      this.loanService.simulateLoan(loanAmount, monthlyIncome, repaymentRatio)
        .subscribe({
          next: (result) => {
            this.simulationResult = result;
          },
          error: (error) => {
            console.error('Error simulating loan:', error);
          }
        });
    }
  }
}
<div class="container mt-4">
  <div class="card">
    <div class="card-header">
      <h2>Loan Simulator</h2>
    </div>
    <div class="card-body">
      <form [formGroup]="simulatorForm" (ngSubmit)="onSubmit()">
        <div class="mb-3">
          <label for="loanAmount" class="form-label">Loan Amount</label>
          <input
            type="number"
            id="loanAmount"
            formControlName="loanAmount"
            class="form-control"
            placeholder="Enter loan amount"
          >
          <div class="invalid-feedback" *ngIf="simulatorForm.get('loanAmount')?.invalid && simulatorForm.get('loanAmount')?.touched">
            Please enter a valid loan amount.
          </div>
        </div>

        <div class="mb-3">
          <label for="monthlyIncome" class="form-label">Monthly Income</label>
          <input
            type="number"
            id="monthlyIncome"
            formControlName="monthlyIncome"
            class="form-control"
            placeholder="Enter monthly income"
          >
          <div class="invalid-feedback" *ngIf="simulatorForm.get('monthlyIncome')?.invalid && simulatorForm.get('monthlyIncome')?.touched">
            Please enter a valid monthly income.
          </div>
        </div>

        <div class="mb-3">
          <label for="repaymentRatio" class="form-label">Repayment Ratio</label>
          <input
            type="number"
            id="repaymentRatio"
            formControlName="repaymentRatio"
            class="form-control"
            step="0.1"
            min="0"
            max="1"
          >
          <div class="invalid-feedback" *ngIf="simulatorForm.get('repaymentRatio')?.invalid && simulatorForm.get('repaymentRatio')?.touched">
            Please enter a valid repayment ratio between 0 and 1.
          </div>
        </div>

        <button type="submit" class="btn btn-primary" [disabled]="!simulatorForm.valid">
          Simulate Loan
        </button>
      </form>

      <div class="mt-4" *ngIf="simulationResult">
        <h3>Simulation Result</h3>
        <div class="card">
          <div class="card-body">
            <p>{{simulationResult.message}}</p>
            <p>Monthly Repayment: ${{simulationResult.monthlyRepayment}}</p>
            <p>Duration (Months): {{simulationResult.durationMonths}}</p>
            <p>Duration (Weeks): {{simulationResult.durationWeeks}}</p>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
