import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { RouterModule } from '@angular/router';
import { LoanService } from '../../services/loan.service';

@Component({
  selector: 'app-loan-recommendation',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, RouterModule],
  templateUrl: './loan-recommendation.component.html',
  styleUrls: ['./loan-recommendation.component.css']
})
export class LoanRecommendationComponent {
  recommendationForm: FormGroup;
  recommendation: string | null = null;

  constructor(
    private fb: FormBuilder,
    private loanService: LoanService
  ) {
    this.recommendationForm = this.fb.group({
      income: ['', [Validators.required, Validators.min(0)]]
    });
  }

  onSubmit() {
    if (this.recommendationForm.valid) {
      const { income } = this.recommendationForm.value;
      this.loanService.getRecommendedLoan(income)
        .subscribe({
          next: (result) => {
            this.recommendation = result.message;
          },
          error: (error) => {
            console.error('Error getting loan recommendation:', error);
            this.recommendation = 'Error getting loan recommendation. Please try again.';
          }
        });
    }
  }
}
<div class="container mt-4">
  <div class="card">
    <div class="card-header">
      <h2>Loan Recommendation</h2>
    </div>
    <div class="card-body">
      <form [formGroup]="recommendationForm" (ngSubmit)="onSubmit()">
        <div class="mb-3">
          <label for="income" class="form-label">Monthly Income</label>
          <input
            type="number"
            id="income"
            formControlName="income"
            class="form-control"
            placeholder="Enter your monthly income"
          >
          <div class="invalid-feedback" *ngIf="recommendationForm.get('income')?.invalid && recommendationForm.get('income')?.touched">
            Income is required and must be greater than 0
          </div>
        </div>

        <button type="submit" class="btn btn-primary" [disabled]="!recommendationForm.valid">
          Get Recommendation
        </button>
      </form>

      <div class="mt-4" *ngIf="recommendation">
        <h3>Recommendation</h3>
        <div class="card">
          <div class="card-body">
            <p>{{recommendation}}</p>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
