import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink, RouterLinkActive } from '@angular/router';

@Component({
  selector: 'app-navbar',
  standalone: true,
  imports: [CommonModule, RouterLink, RouterLinkActive],
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.scss']
})
export class NavbarComponent {}

<nav class="navbar">
  <ul class="nav-list">
    <li><a routerLink="/loans" routerLinkActive="active">Loans</a></li>
    <li><a routerLink="/loans/new" routerLinkActive="active">New Loan</a></li>
    <li><a routerLink="/simulator" routerLinkActive="active">Loan Simulator</a></li>
    <li><a routerLink="/recommendation" routerLinkActive="active">Loan Recommendation</a></li>
    <li><a routerLink="/repayment" routerLinkActive="active">Loan Repayment</a></li>
  </ul>
</nav>
